import React, { useState, useEffect } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import TruncatedText from './TruncatedText';

const FileUploadForm = () => {
  const initialValues = { file: null };
  const [orignalText, setOrignalText] = useState(null);
  const [summeryText, setSummeryText] = useState(null);
  const [loading, setLoading] = useState(false);
  const [displayedWordIndex, setDisplayedWordIndex] = useState(0);

  const validationSchema = Yup.object({
    file: Yup.mixed()
      .required('File is required')
      .test(
        'fileFormat',
        'Invalid file format. Only PDF files are allowed.',
        (value) => value && value.type === 'application/pdf'
      ),
  });

  const onSubmit = async (values) => {
    try {
      setLoading(true); // Set loading to true on form submission

      const formData = new FormData();
      formData.append('file', values.file);

      const response = await axios.post('http://127.0.0.1:5000/api/upload', formData);
      let jsonData = response.data;

      if (response) {
        setOrignalText(jsonData.original_text);
        setSummeryText(jsonData.resulted_text.summary_text);
      }

    } catch (error) {
      console.error('Error uploading file:', error);
    } finally {
      setLoading(false); // Set loading to false after API call completes
    }
  };

  const handleChange = () => {
    // Handle any additional logic on button click
  };

  useEffect(() => {
    // Display words one by one with a delay of 500 milliseconds
    const intervalId = setInterval(() => {
      setDisplayedWordIndex((prevIndex) => prevIndex + 1);
    }, 500);

    if (summeryText) {
      if (displayedWordIndex === summeryText.split(' ').length) {
        clearInterval(intervalId);
      }
    }

    // Cleanup function to clear the interval when the component unmounts
    return () => clearInterval(intervalId);
  }, [displayedWordIndex, summeryText]);

  return (
    <div className='mt-5'>
      <form onSubmit={formik.handleSubmit}>
        {/* ... (existing form code) */}

        <div className={loading ? "displayLoader" : "hideLoader"}>
          <div className='spinner-border mt-3' role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>

        <div>
          <button type="submit" className="btn btn-primary mt-3" onClick={handleChange}>
            Submit
          </button>
        </div>

      </form>
      {/* ... (existing code) */}
    </div>
  );
};

export default FileUploadForm;
