import React, { useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

const FileUploadForm = () => {
    const initialValues = { file: null };
    const [summeryData, SetSummeryData] = useState({})

    const validationSchema = Yup.object({
        file: Yup.mixed().required('File is required').test(
            'fileFormat',
            'Invalid file format. Only PDF files are allowed.',
            (value) => value && value.type === 'application/pdf'
        ),
    });

    const onSubmit = async (values) => {
        try {
            const formData = new FormData();
            formData.append('file', values.file);

            const response = await axios.post('http://127.0.0.1:5000/api/upload', formData);

            SetSummeryData(JSON.stringify(response))
            console.log(response);
        } catch (error) {
            console.error('Error uploading file:', error);
        }
    };

    const formik = useFormik({
        initialValues,
        validationSchema,
        onSubmit,
    });

    return (
        <form onSubmit={formik.handleSubmit}>
            <div>
                <label htmlFor="file" className="form-label">Upload PDF for Text Summarization</label>
                <input
                    id="file"
                    name="file"
                    type="file"
                    className="form-control"
                    onChange={(event) => formik.setFieldValue('file', event.target.files[0])}
                    onBlur={formik.handleBlur}
                />
                {formik.touched.file && formik.errors.file ? (
                    <div style={{ color: 'red' }}>{formik.errors.file}</div>
                ) : null}
            </div>

            <div className={ !summeryData ? "displayLoader" : "hideLoader"}>
                <div className='spinner-border mt-3'  role="status">
                    <span className="visually-hidden">Loading...</span>
                </div>
            </div>


            <div>
                <button type="submit" className="btn btn-primary mt-3">Submit</button>
            </div>

            <p>
                {Object.entries(summeryData).map(([key, value]) => (
                    <div key={key}>
                        <h3>{key}</h3>
                        {typeof value === 'object' ? (
                            // If the value is an object, map its properties
                            <ul>
                                {Object.entries(value).map(([subKey, subValue]) => (
                                    <li key={subKey}>
                                        <strong>{subKey}:</strong> {subValue}
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            // If the value is not an object, display it directly
                            <p>{value}</p>
                        )}
                    </div>
                ))}
            </p>
        </form>
    );
};

export default FileUploadForm;
